int intID, intPrice;
bool blnGotIndices = false;

using (GenericParser p = new GenericParser())
{
  p.SetDataSource(@"C:\MyData.txt");
  p.FirstRowHasHeader = true;

  while(p.Read())
  {
    if (!blnGotIndices)
    {
      blnGotIndices = true;
      intID = p.GetColumnIndex("ID");
      intPrice = p.GetColumnIndex("Price");
    }
  
